import React, { useState } from 'react'
import { Link } from 'react-router-dom'
import LetterAnimation from './LetterAnimation';

const Home = () => {
  const theText = "Hello there!I'm Dariganwho you looking for.";
  const [letterClass, setLetterClass] = useState('letterAnimation')
  const textNodes = document.querySelectorAll('.text-wrapper *');
  const characters = Array.from(textNodes).flatMap(node => Array.from(node.textContent).filter(char => /\S/.test(char)));

  const strArray = characters.slice(0, 15); // Extracts the first 15 characters

  return (
    <div className="container home">
        <div className="text-wrapper">
            <h1><LetterAnimation letterClass={letterClass} strArray={characters.slice()} /><br />I'm Darigan<br />who you looking for.</h1>
            <h2><span>Web Developer</span> / <span>Software Engineer</span> / <span>CS Student</span></h2>
            <Link to="/contactMe" className="button">Say Hello</Link>
        </div>
    </div>
  )
}

export default Home
