import React, { useEffect, useState } from 'react'
import LetterAnimation from './LetterAnimation';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCss3, faGit, faHtml5, faJs, faPython, faReact, faSquareJs } from '@fortawesome/free-brands-svg-icons';


const About = () => {
  const [letterClass, setLetterClass] = useState('LetterAnimation')

  useEffect(() => {
    const timeoutId = setTimeout(() => {
      setLetterClass('LetterAnimation-hover');
    }, 3000);
    return () => {
      clearTimeout(timeoutId);
    };

  }, []);

  return (
    <div className="container about">
      <div className="text">
        <h1>
          <LetterAnimation strArray={['W', 'h', 'o', ' ', 'a', 'm', ' ', 'I', '?']}
            letterClass={letterClass}
            idx={11} />
        </h1>
